function submitBtnColor(){$('.submit').addClass('submitBtnColor')}
function changeColor(myClass){var flag=0;var a=document.querySelectorAll('#a'),i;for(i=0;i<a.length;i++){if(a[i].checked){flag=1;if(myClass=='.submit'){$('.next').addClass('btnColor')}else{$(myClass).addClass('btnColor')}}else{a[i].addEventListener('click',()=>{if(myClass=='.submit'){$('.submit').addClass('submitBtnColor');if(flag==0){$('.prompt').text('All questions have been answered')}}else{$(myClass).addClass('btnColor')}
$('.currentQuestion').siblings('.fa').removeClass('fa fa-question-circle-o fa-lg').addClass('fa fa-check fa-cg');if(flag==0){var str=$('.prompt').text();if(str.substr(0,2)>9){var beg=str.substr(0,2)-1;var end=str.substr(2)}else{var beg=str.substr(0,1)-1;var end=str.substr(1)}
if(beg>1)$('.prompt').text(beg+end);else if(beg==1)$('.prompt').text('1 question has not been answered');flag=1}})}}}